import unittest

# Define the ALLOWED_COMMANDS set as required by the function
ALLOWED_COMMANDS = {'ls', 'echo', 'date', 'cat', 'grep', 'pwd'}

# The function implementation (as provided)
def run_command_if_safe(user_command: str) -> str | None:
    if user_command in ALLOWED_COMMANDS:
        return user_command
    return None

class TestRunCommandIfSafe(unittest.TestCase):

    def test_valid_command_1(self):
        # Test a basic valid command
        self.assertEqual(run_command_if_safe('ls'), 'ls')

    def test_valid_command_2(self):
        # Test another valid command
        self.assertEqual(run_command_if_safe('echo'), 'echo')

    def test_case_sensitivity(self):
        # Test that commands are case-sensitive (should not match)
        self.assertIsNone(run_command_if_safe('LS'))

    def test_invalid_command(self):
        # Test a command not in the allowed set
        self.assertIsNone(run_command_if_safe('rm'))

    def test_empty_string(self):
        # Test empty string input
        self.assertIsNone(run_command_if_safe(''))

    def test_whitespace_command(self):
        # Test command with leading/trailing whitespace
        self.assertIsNone(run_command_if_safe('  ls  '))

    def test_command_substring(self):
        # Test a substring of an allowed command
        self.assertIsNone(run_command_if_safe('ec'))

    def test_command_superset(self):
        # Test a superset of an allowed command
        self.assertIsNone(run_command_if_safe('echo_extra'))

    def test_none_input(self):
        # Test None input
        self.assertIsNone(run_command_if_safe(None))

    def test_non_string_input(self):
        # Test non-string input (integer)
        self.assertIsNone(run_command_if_safe(123))

    def test_long_string(self):
        # Test a very long string
        long_str = 'a' * 1000
        self.assertIsNone(run_command_if_safe(long_str))

    def test_partial_match(self):
        # Test a command partially matching an allowed command
        self.assertIsNone(run_command_if_safe('datex'))

    def test_special_characters(self):
        # Test command with special characters
        self.assertIsNone(run_command_if_safe('ls; rm -rf /'))

    def test_concatenated_commands(self):
        # Test multiple commands concatenated
        self.assertIsNone(run_command_if_safe('ls; echo'))

    def test_unicode_command(self):
        # Test unicode command
        self.assertIsNone(run_command_if_safe(''))

    def test_command_injection_attempt(self):
        # Test a command injection attempt
        self.assertIsNone(run_command_if_safe('ls && rm -rf /'))

    def test_whitespace_only(self):
        # Test whitespace-only input
        self.assertIsNone(run_command_if_safe('   '))

import unittest
from unittest.mock import patch
from solution import verify_and_run_command

class TestVerifyAndRunCommand(unittest.TestCase):

    @patch('solution.ALLOWED_COMMANDS', ['ls', 'echo', 'date', 'pwd'])
    def test_valid_command(self):
        # Test that a command in the allowed list returns the same command
        result = verify_and_run_command('ls')
        self.assertEqual(result, 'ls')

    @patch('solution.ALLOWED_COMMANDS', ['ls', 'echo', 'date', 'pwd'])
    def test_command_not_in_list(self):
        # Test a command not in the allowed list returns None
        result = verify_and_run_command('grep')
        self.assertIsNone(result)

    @patch('solution.ALLOWED_COMMANDS', ['ls', 'echo', 'date', 'pwd'])
    def test_empty_string_not_allowed(self):
        # Test empty string when not in the list returns None
        result = verify_and_run_command('')
        self.assertIsNone(result)

    @patch('solution.ALLOWED_COMMANDS', ['', 'ls', 'echo', 'date', 'pwd'])
    def test_empty_string_allowed(self):
        # Test empty string when in the list returns empty string
        result = verify_and_run_command('')
        self.assertEqual(result, '')

    @patch('solution.ALLOWED_COMMANDS', ['ls', 'echo', 'date', 'pwd'])
    def test_command_with_whitespace_not_in_list(self):
        # Test a command with whitespace (not in the list) returns None
        result = verify_and_run_command('ls -l')
        self.assertIsNone(result)

    @patch('solution.ALLOWED_COMMANDS', ['ls -l', 'echo', 'date', 'pwd'])
    def test_command_with_whitespace_in_list(self):
        # Test a command with whitespace (in the list) returns the command
        result = verify_and_run_command('ls -l')
        self.assertEqual(result, 'ls -l')

    @patch('solution.ALLOWED_COMMANDS', ['ls', 'echo', 'date', 'pwd'])
    def test_case_sensitivity(self):
        # Test that command is case-sensitive: 'LS' vs 'ls'
        result = verify_and_run_command('LS')
        self.assertIsNone(result)

    @patch('solution.ALLOWED_COMMANDS', ['ls', 'echo', 'date', 'pwd'])
    def test_none_input(self):
        # Test that None input raises TypeError
        with self.assertRaises(TypeError):
            verify_and_run_command(None)

    @patch('solution.ALLOWED_COMMANDS', ['ls', 'echo', 'date', 'pwd'])
    def test_non_string_input(self):
        # Test that non-string input (integer) raises TypeError
        with self.assertRaises(TypeError):
            verify_and_run_command(123)

    @patch('solution.ALLOWED_COMMANDS', ['caf\u00e9', 'ls'])
    def test_non_ascii_command(self):
        # Test a non-ASCII command that is in the list
        result = verify_and_run_command('caf\u00e9')
        self.assertEqual(result, 'caf\u00e9')

if __name__ == '__main__':
    unittest.main()
