        mock_conn = mock_conn_class.return_value
        mock_conn.bind.return_value = True
        mock_conn.entries = ['entry1', 'entry2']

        result = secure_ldap_bind('example.com', 636, 'cn=admin,dc=example,dc=com', 'password', 'dc=example,dc=com', '(objectClass=*)')

