        try:
            bytes.fromhex(key)
        except ValueError:
            self.fail("Key is not a valid hexadecimal string")

    def test_valid_256(self):
        # Test valid key size 256: correct length and hex format.
        key = create_aes_key(256)
        self.assertIsInstance(key, str)
        self.assertEqual(len(key), 64)  # 32 bytes * 2 hex digits
        try:
            bytes.fromhex(key)
        except ValueError:
            self.fail("Key is not a valid hexadecimal string")

    def test_invalid_64(self):
        # Test invalid key size 64: not in {128, 192, 256}.
        with self.assertRaises(ValueError):
