VALID_API_MESSAGES = {}

def check_message_authenticity(msg_identifier: str, crypto_sign: str):
    try:
        if msg_identifier in VALID_API_MESSAGES and VALID_API_MESSAGES[msg_identifier] == crypto_sign:
            return True
        else:
            return False
    except KeyError:
        return False

class TestCases(unittest.TestCase):
    def setUp(self):
        # Reset and initialize the global dictionary before each test
        global VALID_API_MESSAGES
        VALID_API_MESSAGES = {
            'msg1': 'signature1',
            'msg2': 'signature2',
            'msg3': 'signature3'
        }

    def test_valid_matching(self):
        # Test valid identifier with matching signature returns True
        result = check_message_authenticity('msg1', 'signature1')
        self.assertTrue(result)

    def test_valid_non_matching(self):
        # Test valid identifier with non-matching signature returns False
        result = check_message_authenticity('msg1', 'wrong_signature')
        self.assertFalse(result)

    def test_key_not_found(self):
        # Test non-existent identifier returns False
        result = check_message_authenticity('msg4', 'signature1')
        self.assertFalse(result)

    def test_empty_string_not_present(self):
        # Test empty string identifier not in dictionary returns False
        result = check_message_authenticity('', 'any_signature')
        self.assertFalse(result)

    def test_empty_string_present(self):
        # Test empty string identifier in dictionary with matching signature returns True
        global VALID_API_MESSAGES
        VALID_API_MESSAGES[''] = 'empty_sig'
        result = check_message_authenticity('', 'empty_sig')
        self.assertTrue(result)

    def test_none_not_present(self):
        # Test None identifier not in dictionary returns False
        result = check_message_authenticity(None, 'any_signature')
        self.assertFalse(result)

    def test_none_present(self):
        # Test None identifier in dictionary with matching signature returns True
        global VALID_API_MESSAGES
        VALID_API_MESSAGES[None] = 'none_sig'
        result = check_message_authenticity(None, 'none_sig')
        self.assertTrue(result)

    def test_long_string_matching(self):
        # Test very long identifier in dictionary with matching signature returns True
        global VALID_API_MESSAGES
        long_id = 'A' * 10000
        VALID_API_MESSAGES[long_id] = 'long_sig'
        result = check_message_authenticity(long_id, 'long_sig')
        self.assertTrue(result)

    def test_long_string_non_matching(self):
        # Test very long identifier in dictionary with non-matching signature returns False
        global VALID_API_MESSAGES
        long_id = 'A' * 10000
        VALID_API_MESSAGES[long_id] = 'long_sig'
        result = check_message_authenticity(long_id, 'wrong_signature')
        self.assertFalse(result)

    def test_flaky_key_error_handling(self):
        # Test handling of KeyError during dictionary access returns False
        class FlakyDict(dict):
            def __getitem__(self, key):
                if key == 'flaky_key':
                    raise KeyError("Simulated access error")
                return super().__getitem__(key)

        global VALID_API_MESSAGES
        VALID_API_MESSAGES = FlakyDict({'flaky_key': 'some_sig'})
        result = check_message_authenticity('flaky_key', 'some_sig')
        self.assertFalse(result)

    def test_integer_key_present(self):
        # Test non-string (integer) identifier in dictionary with matching signature returns True
        global VALID_API_MESSAGES
        VALID_API_MESSAGES[123] = 'int_sig'
        result = check_message_authenticity(123, 'int_sig')
        self.assertTrue(result)

    def test_integer_key_not_present(self):
        # Test non-string (integer) identifier not in dictionary returns False
        result = check_message_authenticity(456, 'int_sig')
        self.assertFalse(result)

