        # Test a malicious URL at the beginning returns True.
        self.assertTrue(analyze_scheme("javascript:alert('XSS');"))

    def test_malicious_url_middle(self):
        # Test a malicious URL in the middle returns True.
        self.assertTrue(analyze_scheme("Visit javascript:alert(1) for details."))

    def test_safe_image_data_url_beginning(self):
        # Test a safe image data URL at the beginning returns False.
        self.assertFalse(analyze_scheme("data:image/png;base64,iVBORw0KGgo="))

