        # Test an unsafe image data URL at the beginning returns True.
        self.assertTrue(analyze_scheme("data:image/svg+xml;base64,PHN2ZyB4bWxucz0="))

    def test_empty_string(self):
        # Test an empty string returns False.
        self.assertFalse(analyze_scheme(""))

