        self.assertTrue(analyze_scheme("javascript:alert(1); vbscript:alert(2);"))

    def test_malicious_scheme_in_comment(self):
        # Test a malicious scheme in a comment returns True.
        self.assertTrue(analyze_scheme("This is dangerous: javascript:void(0);"))

    def test_case_insensitivity_safe_image(self):
        # Test case insensitivity for safe image data URL returns False.
